mapper.select:
SELECT
c.categoryId,c.entId,c.categorycode,c.categoryname,c.parentId,c.parentcode,c.level,c.erpcode,
CASE
WHEN c.leafFlag = 1 THEN
	0
WHEN c.leafFlag = 0 THEN
	1 ELSE NULL
END AS leafFlag
FROM
category c WHERE c.entId=#{entId}

	AND c.categoryId = #{cid}


	AND c.categorycode = #{code}


	AND c.categoryname = #{name}


	AND c.parentcode = #{parent_code}


	AND c.level = #{mcclass}


	AND c.leafFlag = #{is_parent}


	AND c.erpcode = #{jygs}


	limit ${page_no},${page_size}

SELECT
count(1)
FROM
category c where c.entId=#{entId}

	AND c.categoryId = #{cid}


	AND c.categorycode = #{code}


	AND c.categoryname = #{name}


	AND c.parentcode = #{parent_code}


	AND c.level = #{mcclass}


	AND c.leafFlag = #{is_parent}


	AND c.erpcode = #{jygs}
