mapper.select:
SELECT
	s.ssgid,s.entId,s.erpCode,s.goodsCode,s.barNo,s.goodsName,
	s.goodsType,s.categoryName,s.brandName,s.saleSpec,s.saleUnit,
	s.salePrice,s.vid,s.goodsStatus,b.brandCode,c.categoryCode
FROM
	saleGoods s
LEFT JOIN
	brandInfo b
ON  
	s.brandId=b.brandId 
AND 
	s.entId=b.entId
LEFT JOIN
	category c
ON  
	s.categoryId=c.categoryId 
AND 
	s.entId=c.entId 
WHERE 
	s.entId=#{entId}

	AND s.erpCode = #{jygs}


	AND s.ssgid = #{code}


	AND s.goodsCode = #{item_code}


	AND s.barNo = #{barcode}


	AND s.goodsType = #{typeid}


	AND s.goodsStatus = #{status}


	AND s.vid = #{vendor_id}


	AND c.categoryCode = #{cat_code}


	AND b.brandCode = #{brand_code}


	limit ${page_no},${page_size}

SELECT
	count(1)
FROM
	saleGoods s
LEFT JOIN
	brandInfo b
ON  
	s.brandId=b.brandId 
AND 
	s.entId=b.entId
LEFT JOIN
	category c
ON  
	s.categoryId=c.categoryId 
AND 
	s.entId=c.entId 
WHERE 
	s.entId=#{entId}

	AND s.erpCode = #{jygs}


	AND s.ssgid = #{code}


	AND s.goodsCode = #{item_code}


	AND s.barNo = #{barcode}


	AND s.goodsType = #{typeid}


	AND s.goodsStatus = #{STATUS}


	AND s.vid = #{vendor_id}


	AND c.categoryCode = #{cat_code}


	AND b.brandCode = #{brand_code}
