mapper.select:
SELECT
	s.entId,s.saleOrgId,s.shopId,s.orgCode,s.parentId,s.orgName,s.erpCode,p.shopCode
FROM
	saleOrg s 
LEFT JOIN
	shop p 
ON  
	s.shopId=p.shopId 
AND 
	s.entId=p.entId 
WHERE 
	s.entId=#{entId}

	AND s.saleOrgId = #{oid}


	AND s.orgCode = #{code}


	AND s.orgName = #{name}


	AND s.erpCode = #{jygs}


	AND s.parentId = #{parent_id}


	AND p.shopCode = #{org_code}


	limit ${page_no},${page_size}

SELECT
	count(1)
FROM
	saleOrg s 
LEFT JOIN
	shop p 
ON  
	s.shopId=p.shopId 
AND 
	s.entId=p.entId 
WHERE 
	s.entId=#{entId}

	AND s.saleOrgId = #{oid}


	AND s.orgCode = #{code}


	AND s.orgName = #{name}


	AND s.erpCode = #{jygs}


	AND s.parentId = #{parent_id}


	AND p.shopCode = #{org_code}
